import java.util.Scanner;

public class While_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int num=4;
		 int i=0;
		 while(i <= num)
		 {
			int j=0;
			 while(j<=i) {
			 System.out.print("*");
			 j++;
			 }
			 System.out.println("");
			 i++;
		 }
		 
		 
	
}
}